import xml.etree.ElementTree as xmlclass Journal_entry():  def __init__(self, entry_element):    self.id = entry_element.get("id")    self.title = (entry_element.find("title")).text    self.text = (entry_element.find("content")).text    self.locked = True      def get_id(self):    return self.id    def get_title(self):    return self.title      def get_text(self):    return self.text      def unlock(self):    self.locked = False      def is_locked(self):    return self.locked